package com.Techment.demo;

public class TechmentAssignment34 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter =0;
		for(int j =1;j<=7;j++)
		{
		for(int i =7;i>counter;i--)
		{
			System.out.print(i);
			
		}
		counter++;
		System.out.println();
		}
	}

}
